mapper.insert:
insert into user ( username, nickname,
  roles, phonenumber, password
  )
values (#{username,jdbcType=VARCHAR}, #{nickName,jdbcType=VARCHAR},
  #{roles,jdbcType=VARCHAR}, #{phonenumber,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}
  )

mapper.select:
select user_id, username, nickname, roles, phonenumber, password
from user
where user_id = #{userId}
